import React, { useState } from 'react'
import { useStore } from './dummy'
import { useNavigate } from 'react-router-dom';

const Description = () => {

    const navigate = useNavigate()
    const [cart, setCart] = useState<string>()

    const currentImage = useStore((state:any) => state.currentImage)
    const currentPhone = useStore((state:any) => state.currentPhone)
    const currentPrice = useStore((state:any) => state.currentPrice)
return (
    <div className='p-10 flex justify-center'>
            <div className='border-2 border-red-200 p-10 w-min text-center flex flex-row justify-evenly'>
                    <img src={currentImage} width="300px" height="505px"></img>
                    <div>
                            <div className='border-2 h-fit p-10 border-blue-700 self-center m-5'>
                                    <h3 className=''>{currentPhone}</h3>
                                    <br></br>
                                    <h3>Price: {currentPrice}</h3>
                            </div>
                            <button className='bg-blue-700 text-white p-2 rounded-xl mt-4 m-6' onClick={()=>navigate('/')}>Back</button>
                            <button className='bg-blue-700 text-white p-2 rounded-xl mt-4' onClick={()=>setCart("✅ Phone added to cart")}>Add to cart</button>
                            <br/>
                            <h2 className={`ml-14 p-2 ${cart ? 'bg-green-400' : ''} text-white w-fit`}>{cart}</h2>
                    </div>
            </div>
    </div>
)
}

export default Description
