import React from 'react'
import { useQuery } from 'react-query'
import { useNavigate } from 'react-router-dom';
import { create } from 'zustand';

const Dummy = () => {

    const mobilecatalogue = useQuery('mobilecatalogue', async () => {
        const response = await fetch('https://pcat.mobile.xfinity.com/products?category=DEVICE&limit=23');
        return response.json();
    });

    const navigate = useNavigate()

    const useStore = create((set) => ({
        currentPhone: phone.products[i].name,
        currentImage: phone.products[i].image.url,
        currentPrice: phone.products[i].prices[0].originalPrice,
    }))

    if(mobilecatalogue.isLoading){
        return <h1>Loading...</h1>
      }

    const phone = mobilecatalogue.data
    console.log(phone.products)





    return (
        <>
        Mobile catalogue
        <div className='flex flex-row flex-wrap'>
            {Array.from({ length: phone.products.length }).map((_, i) => (
                <div key={i}>
                    <div className=' bg-slate-white border-2 border-red-200 rounded-xl p-6 w-fit m-4 hover:border-blue-700 text-center' onClick={()=>navigate(`/${phone.products[i].name.replace(/ /g, '_')}`)}>
                                <img src={phone.products[i].image.url} width="250" height="250"/>
                                <h3>{phone.products[i].name}</h3>
                                <h3>{phone.products[i].prices[0].originalPrice}</h3>
                    </div>
                </div>
            ))}
        </div>  
        </>
    )
}

export default Dummy
