import React, { useState } from 'react'
import { useStore } from './dummy'
import { useNavigate } from 'react-router-dom';

const Description = () => {

    const navigate = useNavigate()
    const [cart, setCart] = useState<string>()

    const currentBrand = useStore((state:any) => state.currentBrand)
    const currentImage = useStore((state:any) => state.currentImage)
    const currentPhone = useStore((state:any) => state.currentPhone)
    const currentPrice = useStore((state:any) => state.currentPrice)
    const currentOS = useStore((state:any) => state.currentOS)

return (
    <div className='flex flex-col items-center justify-center w-fit lg:w-screen'>
    <div className='m-20 border-2 border-red-200 p-10 w-auto h-auto justify-evenly md:flex md:flex-row md:justify-evenly items-center md:m-10 flow-root'>
        <img className='md:min-w-[300px] md:max-w-[550px] w-50%' src={currentImage}></img>
        <div>
            <div className='border-2 w-fit md:h-20% md:text-4xl p-10 md:p-24 border-blue-700 m-5 flex flex-col'>
                <h3 className=' font-bold'>{currentBrand}</h3>
                <h3 className=''>{currentPhone}</h3>
                <br></br>
                <h3>OS: {currentOS}</h3>
                <br></br>
                <h3>Price: {currentPrice}</h3>
            </div>
            <div className='flex flex-col justify-center md:flex-none'>
                <button className='bg-blue-400 text-white p-2 rounded-xl mt-4 m-6 hover:bg-blue-700' onClick={()=>navigate('/')}>Back</button>
                <button className='bg-blue-400 text-white p-2 rounded-xl mt-4  hover:bg-blue-700' onClick={()=>setCart("✅ Phone added to cart")}>Add to cart</button>
                <br/>
                <h2 className={`self-center p-2 ${cart ? 'bg-green-400' : ''} text-white w-fit rounded-xl`}>{cart}</h2>
            </div>
        </div>
    </div>
</div>
)
}

export default Description
