import React from 'react'
import { useQuery } from 'react-query'
import { useNavigate, Link } from 'react-router-dom';
import { create } from 'zustand';

export const useStore = create(set => ({
    currentPhone: '',
    currentImage: '',
    currentPrice: 0,
  }));

const Dummy = () => {

    const mobilecatalogue = useQuery('mobilecatalogue', async () => {
        const response = await fetch('https://pcat.mobile.xfinity.com/products?category=DEVICE&limit=23');
        return response.json();
    });

    const navigate = useNavigate()



    if(mobilecatalogue.isLoading){
        return <h1>Loading...</h1>
      }

    const phone = mobilecatalogue.data
    console.log(phone.products)





    return (
        <>
        Mobile catalogue
        <div className='flex flex-row flex-wrap'>
        {phone.products.map((product, i) => (
            <Link
            key={i}
            to={`/${product.name.replace(/ /g, '_')}`} // Pass phone name as part of the URL
            className='bg-slate-white border-2 border-red-200 rounded-xl p-6 w-fit m-4 hover:border-blue-700 text-center'
            >
            <img src={product.image.url} width="250" height="250" alt={product.name} />
            <h3>{product.name}</h3>
            <h3>{product.prices[0].originalPrice}</h3>
            </Link>
  ))}
</div>  
        </>
    )
}

export default Dummy
